package com.konoyonosubeteganikuichan.vtscontrolleroyo

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.MotionEvent
import android.widget.Button
import android.widget.EditText
import java.net.URI

class MainActivity : AppCompatActivity() {
    private var client: VtsWebSocketClient? = null
    val pluginName = "ｫｮ~"
    val pluginDeveloper = "この世のすべてが憎いちゃんの栽培は法律で禁止されています"
    val requestId = "oyoyoID"
    val interval = 100

    private var moveX = 0f
    private var moveY = 0f
    private var rot = 0f
    var lastSendTime = 0L

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
    }

    override fun onResume() {
        super.onResume()
        val hostEditText = findViewById<EditText>(R.id.hostEditText)
        // input your own VTS web socket address
        hostEditText.setText("ws://192.168.11.7:8001/")  // todo: save to SharedPreferences

        findViewById<Button>(R.id.btnConnect).setOnClickListener { btn ->
            val uri = URI("${hostEditText.text}")
            client = VtsWebSocketClient(this, uri) { status ->
                findViewById<Button>(R.id.btnSendMessage).text = status
            }
            client?.connect()
            btn.isEnabled = false
            findViewById<Button>(R.id.btnDisconnect).isEnabled = true
            findViewById<Button>(R.id.btnSendMessage).isEnabled = true
            client?.status = "INIT"
        }

        // 切断ボタンが押下された時
        findViewById<Button>(R.id.btnDisconnect).setOnClickListener { btn ->
            client?.close()
            findViewById<Button>(R.id.btnConnect).isEnabled = true
            findViewById<Button>(R.id.btnSendMessage).isEnabled = false
            btn.isEnabled = false
        }

        // 送信ボタンが押下された時
        findViewById<Button>(R.id.btnSendMessage).setOnClickListener {
            when(client?.status) {
                "INIT" -> {
                    // https://github.com/DenchiSoft/VTubeStudio
                    val startRequest = "{" +
                            "\"apiName\": \"VTubeStudioPublicAPI\"," +
                            "\"apiVersion\": \"1.0\"," +
                            "\"requestID\": \"$requestId\"," +
                            "\"messageType\": \"APIStateRequest\"" +
                            "}"
                    client?.send(startRequest)
                }
                "AUTH_START" -> {
                    // input your icon (b64 encoded)
                    val icon = ""
                    val authenticationTokenRequest = "{" +
                            "\"apiName\": \"VTubeStudioPublicAPI\"," +
                            "\"apiVersion\": \"1.0\"," +
                            "\"requestID\": \"$requestId\"," +
                            "\"messageType\": \"AuthenticationTokenRequest\"," +
                            "\"data\": {" +
                            "    \"pluginName\": \"$pluginName\"," +
                            "    \"pluginDeveloper\": \"$pluginDeveloper\"," +
                            "    \"pluginIcon\": \"$icon\"" +
                            "}" +
                            "}"
                    client?.send(authenticationTokenRequest)
                }
                "TOKEN_RECEIVED" -> {
                    val token = client?.token
                    // input your pluginName and developerName
                    val authenticationRequest = "{" +
                            "\"apiName\": \"VTubeStudioPublicAPI\"," +
                            "\"apiVersion\": \"1.0\", " +
                            "\"requestID\": \"$requestId\", " +
                            "\"messageType\": \"AuthenticationRequest\", " +
                            "\"data\": {" +
                            "    \"pluginName\": \"$pluginName\"," +
                            "    \"pluginDeveloper\": \"$pluginDeveloper\"," +
                            "    \"authenticationToken\": \"${token}\"" +
                            "}" +
                            "}"
                    client?.send(authenticationRequest)
                }
                "AUTH_FINISHED" -> {
                    sendMove()
                }
            }
        }

    }
    override fun onTouchEvent(e: MotionEvent): Boolean {
            when (e.action) {
            MotionEvent.ACTION_MOVE -> {
                val now = System.currentTimeMillis()
                if (lastSendTime < now - interval) {
                    lastSendTime = now
                    val px = moveX
                    val py = moveY
                    moveX = e.x / 1000 - 1
                    moveY = e.y / -1000 + 1
                    if (moveX > 1) moveX = 1f
                    if (moveY > 1) moveY = 1f
                    rot = ((Math.atan2(-(moveY - py).toDouble(), (moveX - px).toDouble()).toFloat() + 1.57f) * 180) / 3.14f
                    sendMove()
                }
            }
        }
        return false
    }

    private fun sendMove() {
        val setParams = "{\n" +
                "    \"apiName\": \"VTubeStudioPublicAPI\",\n" +
                "    \"apiVersion\": \"1.0\",\n" +
                "    \"requestID\": \"SomeID\",\n" +
                "    \"messageType\": \"MoveModelRequest\",\n" +
                "    \"data\": {\n" +
                "        \"timeInSeconds\": 0.2,\n" +
                "        \"valuesAreRelativeToModel\": false,\n" +
                "        \"positionX\": $moveX,\n" +
                "        \"positionY\": $moveY,\n" +
                "        \"rotation\": $rot\n" +
                "    }\n" +
                "}"
        client?.send(setParams)
    }
}
